local mykey = "forkingshirt"

--local f = io.open('words.txt','w+')
--f:write(encrypt(table.concat(string.split([[ words ]],'\n'), '.'),mykey))

local decrypted = decrypt(words, mykey)
local word_table = decrypted:split('.')
local word_dict = {}
local storeWord, hasWord
storeWord= function(str, dict)
    dict = dict or word_dict
    local char1 = string.sub(str, 1, 1)
    local rest = string.sub(str, 2)
    if not dict[char1] then
        dict[char1] = {}
    end
    if string.len(str) > 1 then
        storeWord(rest, dict[char1])
    else 
        dict[char1]['end'] = true 
    end
end
for _, word in ipairs(word_table) do
    storeWord(word)
end

hasWord = function(str, dict, i, len)
    if not dict then
        local res = {}
        for c = 1, string.len(str) do
            local has, _i, _len = hasWord(str, word_dict, c, 0)
            if has then 
                local whitelisted = false
                for _, wl_word in ipairs(badword.whitelist) do
                    if wl_word == string.sub(str, _i, _i + _len - 1) then 
                        whitelisted = true 
                    end
                end
                if not whitelisted then
                    table.insert(res, {_i, _len})
                end
            end
        end
        if #res > 0 then return res end
        return nil
    else
        local char1 = string.sub(str, i, i)
        if type(dict[char1]) == 'table' then -- move in further
            return hasWord(string.sub(str, 2), dict[char1], i, len + 1)
        end
        return (dict and dict['end'] == true), i, len
    end
end

